package ucmoudles.systemback.config.spring;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.ViewControllerRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import ucmoudles.systemback.config.interceptors.RequestInterceptor;/** * @author 9527 * @ClassName CustomSpringWebConfiguration * @Date 2020/1/21 * @Description TODO * @Version 1.0 */@Configurationpublic class CustomSpringWebConfiguration implements WebMvcConfigurer {    @Autowired    private RequestInterceptor requestInterceptor;    @Override    public void addViewControllers(ViewControllerRegistry registry) {        registry.addViewController("/login").setViewName("system/pages/authentication/sign-in");        registry.addViewController("/").setViewName("system/pages/extra/blank-page");        registry.addViewController("/login/success").setViewName("system/dashboard/index");        registry.addViewController("/login/error").setViewName("system/pages/authentication/pages-maintenance");    }}