package ucmoudles.systemback.config.spring;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.servlet.NoHandlerFoundException;import ucmoudles.systemback.exceptions.CommonBusinessException;import ucmoudles.systemback.exceptions.FeignFailException;import ucmoudles.systemback.exceptions.HttpRequestException;/** * @author 9527 * @ClassName ExceptionHandle * @Date 2020/2/17 * @Description TODO * @Version 1.0 */@ControllerAdvicepublic class ExceptionHandle {    @ExceptionHandler(Exception.class)    public String handleEception(Exception e){            return "system/pages/extra/pages-error-500";    }    @ExceptionHandler(NoHandlerFoundException.class)    public String handleNoHandlerFoundException(Exception e){        return "system/pages/extra/pages-error";    }    @ExceptionHandler(HttpRequestException.class)    public String handleHttpRequestException(Exception e){        return "system/pages/extra/pages-error";    }    @ExceptionHandler(FeignFailException.class)    public String handleFeignFailException(Exception e){        return "/system/pages/extra/pages-maintenance";    }    @ExceptionHandler(CommonBusinessException.class)    public String handleBusinessEception(Exception e){        return "system/pages/extra/pages-error-500";    }}