package ucmoudles.systemback.controller.system;import com.alibaba.fastjson.JSONObject;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import ucmoudles.systemback.business.V5Business;import ucmoudles.systemback.config.annotations.SkipSecretKey;import ucmoudles.systemback.constant.V5Constant;import javax.servlet.http.HttpServletRequest;/** * @author 9527 * @ClassName IndexController * @Date 2020/2/3 * @Description TODO * @Version 1.0 */@Controllerpublic class V5Controller {    private Logger logger = LoggerFactory.getLogger(V5Controller.class);    @Autowired    private V5Business v5Business;    @RequestMapping(value = "/v5/info",method = {RequestMethod.GET})    @ResponseBody    @SkipSecretKey    public JSONObject v5Info(HttpServletRequest request) throws Exception {            JSONObject json = v5Business.v5Token();            return json;    }    @RequestMapping(value = "/v5/verify" ,method = {RequestMethod.POST})    @ResponseBody    @SkipSecretKey    public String verify( HttpServletRequest request) throws Exception {        String verifyid = request.getParameter("verifyid");        String token = request.getParameter("token");        String username = request.getParameter("username");        String pwd = request.getParameter("pwd");        String result;        result = v5Business.verify(V5Constant.v5Host,verifyid,token,username,pwd);        return result;    }    public static void main(String[] args) {        System.out.println(new BCryptPasswordEncoder().encode("admin"));;    }}